import { useContext, useState } from "react";
import { Context } from "../../context/Context";
import './write.css';

export default function Write() {
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [file, setFile] = useState(null);
    const { user } = useContext(Context);


    const handleSubmit = async (e) => {
        e.preventDefault();
        const newPost = {
          username: user.username,
          title,
          desc,
        };
        if (file) {
          const data =new FormData();
          const filename = Date.now() + file.name;
          data.append("name", filename);
          data.append("file", file);
          newPost.photo = filename;
          try {
            await axios.post("/upload", data);
          } catch (err) {}
        }
        try {
          const res = await axios.post("/posts", newPost);
          window.location.replace("/post/" + res.data._id);
        } catch (err) {}
      };


    return (
        <div className="write">
            <img  className='writeImg'
            src="https://images.pexels.com/photos/714258/pexels-photo-714258.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" alt="" />
            <form className="writeForm" onSubmit={handleSubmit}>
                <div className="writeFormGroup">
                    <label htmlFor="fileInput">
                    <i className=" writeIcon fas fa-plus-circle"></i>
                    </label>
                    <input type="file" id='fileInput'  style={{display:"none"}}/>
                    <input type="text" placeholder='Title' className='writeInput' />
                </div>
                <div className="writeFormGroup">
                    <textarea placeholder='Write the article here..' type="text" className='writeInput writeText' ></textarea>
                </div>
                <button className="submit" type="submit">Publish</button>
            </form>
        </div>
        )
}
