import './singlepost.css'
import { useLocation } from "react-router";
import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { Context } from "../../context/Context";
import { Link } from 'react-router-dom';




export default function SinglePost () {
    const location = useLocation();
    const path = location.pathname.split("/")[2];
    const [post, setPost] = useState({});
    const PF = "http://localhost:5000/images/";
    const { user } = useContext(Context);
    const [title, setTitle] = useState("");
    const [desc, setDesc] = useState("");
    const [updateMode, setUpdateMode] = useState(false);





    useEffect(() => {
        const getPost = async () => {
          const res = await axios.get("/posts/" + path);
          setPost(res.data);
          setTitle(res.data.title);
          setDesc(res.data.desc);

        };
        getPost();
      }, [path]);


      const handleDelete = async () => {
        try {
          await axios.delete(`/posts/${post._id}`, {
            data: { username: user.username },
          });
          window.location.replace("/");
        } catch (err) {}
      };

      const handleUpdate = async () => {
        try {
          await axios.put(`/posts/${post._id}`, {
            username: user.username,
            title,
            desc,
          });
          setUpdateMode(false)
        } catch (err) {}
      };



    return (
        <div className='singlePost'>
            <div className="singlePostWrapper">
                {
                   post.photo && (
                    <img src={PF + post.photo}
                    alt="" 
                    className='singlePostImg'
                    />

                   ) 
                }{
                    updateMode ? <input  type="text" value={title} className="singlePostTitleInput"/>:(



                 <h1 className="singlePostTitle">
                     {post.title}     
                     {post.username === user?.username && (
                     <div className="editIcons">
                     <i className=" postIcon far fa-edit" onClick={() => setUpdateMode(true)}></i>
                     <i className=" postIcon fas fa-trash-alt"    onClick={handleDelete}></i>
                     </div>
                     )}                

                 </h1>
                    )
                    }
                 <div className="singlePostInfo">
                     <span className="postAuth">Author: 
                     <Link to={`/?user=${post.username}`} className='link'>
                     <b>{post.username}</b>

                     </Link>
                     </span>
                     <span className="postDate">{new Date(post.createdAt).toDateString()}</span>
                 </div>
                 {updateMode ? (
          <textarea
            className="singlePostDescInput"
            value={desc}
            onChange={(e) => setDesc(e.target.value)}
          />
        ) : (
          <p className="singlePostDesc">{desc}</p>
        )}

                 <p className='postDesc'>
                    {post.desc}
                 </p>
            </div>
        </div>
    )
}
